DROP TABLE IF EXISTS `its_login_attempts`;

CREATE TABLE `its_login_attempts` (
	`id` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    `user_id` INT(11) NOT NULL,
    `status` VARCHAR(10) NOT NULL,
    `time` datetime NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Login attempts log.';

DROP TABLE IF EXISTS `its_targets`;

CREATE TABLE `its_targets` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `word` varchar(20) NOT NULL UNIQUE,
  `user_id` INT(11) NOT NULL,
  `time` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Target words used for many HIL studies.';

DROP TABLE IF EXISTS `its_variants`;

CREATE TABLE `its_variants` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `target_id` int(5) NOT NULL,
  `variant` varchar(30) NOT NULL,
  `user_id` INT(11) NOT NULL,
  `time` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Variants for each target word.';

